sources.main.scss:
* {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
}
html {
  font-family: "Comfortaa", cursive;
}
a {
  text-decoration: none;
}

body {
  position: relative;
}

nav {
  height: 56px;
  padding-top: 0.25em;
  .nav-wrapper {
    height: 100%;
    display: flex;
  }

  .brand-logo {
    display: flex;
    height: 100%;
    font-weight: 700;
    color: #000000;
    font-size: 22px;
    align-items: center;
    cursor: pointer;
    img {
      height: 100%;
    }
    span {
      width: 150px;
      display: inline-block;
      padding-left: 8px;
      margin-top: 8px;
    }
  }
  .flex-filler {
    flex-basis: 100%;
  }
  .menu-button {
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    margin-right: 16px;
    div {
      width: 36px;
      height: 4px;
      margin: 0 4px 6px;
      border-radius: 4px;
      background-color: #263238;
    }
  }
}

.stories-container {
  width: 90%;
  margin: 16px auto;
}

.top-stories-title,
.user-stories-title {
  font-size: 28px;
  font-weight: 700;
  color: #263238;
  text-decoration: underline;
}

.story-container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 36px;
  margin-top: 36px;
  color: #000000;
  cursor: pointer;
  div {
    display: inline-block;
  }

  .story-desc {
    display: inline-flex;
    flex-direction: column;
    justify-content: space-between;
    width: 70%;
    height: 100px;
    padding: 8px;
  }
  .story-title {
    margin-top: 8px;
    margin-bottom: 8px;
    display: block;
    font-size: 18px;
    font-weight: 700;
    line-height: 1.2em;
  }
  .story-img {
    height: 100px;
    width: 30%;
    border-radius: 20%;
    overflow: hidden;
    img {
      height: 100%;
      border-radius: 20%;
    }
  }
  .story-category {
    padding: 4px 6px;
    font-weight: 700;
    color: #dd2c00;
    background-color: #fbe9e7;
    border-radius: 4px;
  }
  .story-pub-date {
    padding: 4px 8px;
  }
  &.top-story {
    flex-direction: column-reverse;
    .story-desc {
      width: 100%;
    }
    .story-img {
      width: 100%;
      height: 200px;
      border-radius: 8px;
      img {
        width: 100%;
        margin: 0 auto;
        display: block;
        border-radius: 8px;
      }
    }
  }
}

.container-back {
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 100%;
  background-color: #00000038;
  z-index: 3000;
}
.full-story-container {
  width: 95%;
  margin-left: auto;
  margin-right: auto;
  margin-top: 48px;
  padding: 8px;
  background-color: #ffffff;
  border-radius: 8px;
  box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14),
    0 3px 1px -2px rgba(0, 0, 0, 0.12), 0 1px 5px 0 rgba(0, 0, 0, 0.2);

  .action-btn {
    position: absolute;
    top: 0;
    right: 0;
    padding: 8px;
    margin-top: 32px;
    margin-right: 32px;
    height: 36px;
    width: 36px;
    font-size: 18px;
    font-weight: 200;
    background-color: #ffffff;
    border: none;
    border-radius: 50%;
    color: #dd2c00;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14),
      0 3px 1px -2px rgba(0, 0, 0, 0.12), 0 1px 5px 0 rgba(0, 0, 0, 0.2);
    &:focus {
      outline: none;
    }
    &:hover {
      color: #dd2c00;
    }
  }
  .story-title {
    margin: 16px;
    display: block;
    font-size: 24px;
    font-weight: 700;
    line-height: 1.2em;
  }
  .story-img {
    height: 300px;
    width: 100%;
    border-radius: 4px;
    overflow: hidden;
    img {
      height: 100%;
      width: 100%;
      border-radius: 4px;
    }
  }
  .story-content {
    padding: 8px;
    padding-top: 16px;
    font-size: 20px;
    line-height: 24px;
    font-weight: 300;
  }
  .story-desc {
    margin: 16px;
    div {
      display: inline-block;
      padding: 4px 8px;
      text-transform: capitalize;
      &:first-of-type {
        font-weight: 700;
        color: #dd2c00;
        background-color: #fbe9e7;
        border-radius: 4px;
      }
    }
  }
}
.toast {
  display: none;
  position: fixed;
  top: 60px;
  left: 0;
  right: 0;
  width: fit-content;
  margin: 0 auto;
  padding: 8px;
  font-weight: 500;
  border-radius: 4px;
  color: #dd2c00;
  background-color: #ffffff;
  box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14),
    0 3px 1px -2px rgba(0, 0, 0, 0.12), 0 1px 5px 0 rgba(0, 0, 0, 0.2);
  z-index: 1000;
}

#installBanner {
  display: none;
  position: absolute;
  top: 400px;
  left: 0;
  right: 0;
  width: 90%;
  margin: 0 auto;
  padding: 8px;
  background-color: #ffb847;
  border-radius: 8px;
  box-shadow: 0 4px 5px 0 rgba(0, 0, 0, 0.14), 0 1px 10px 0 rgba(0, 0, 0, 0.12),
    0 2px 4px -1px rgba(0, 0, 0, 0.3);
  .banner-header {
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
  }
  .brand-logo {
    height: 100px;
    font-weight: 700;
    color: #000000;
    font-size: 22px;
    align-items: center;
    cursor: pointer;
    img {
      height: 100%;
    }
  }
  .tagline {
    display: inline-block;
    padding: 8px;
    font-size: 20px;
    font-weight: 500;
    line-height: 24px;
  }
  .user-choice {
    display: flex;
    flex-direction: row;
    justify-content: flex-end;
  }
  button {
    padding: 8px 16px;
    margin: 8px;
    border: none;
    border-radius: 8px;
    min-height: 36px;
    color: #000000;
    font-size: 14px;
    font-weight: 500;
    -webkit-appearance: none;
    background-color: transparent;
    text-transform: uppercase;
    &#installBtn {
      background-color: #ffffff;
    }
    &#cancel {
      border: 1px solid #ffffff;
      background: none;
      font-weight: 400;
    }
  }
}

@media only screen and (max-width: 992px) {
  nav {
    .brand-logo {
      display: flex;
      padding-left: 16px;
    }
  }
}

@media only screen and (min-width: 992px) {
  .app-container {
    width: 90%;
    margin: 0 auto;
  }
  .story-container {
    width: 30%;
    margin-left: 16px;
    margin-right: 16px;
  }
  .stories-group {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
  }
}

footer {
  padding: 16px 16px 8px 16px;
  .copyright {
    font-size: 1.4em;
    font-weight: 700;
    text-align: center;
  }
  .disclaimer {
    text-align: center;
    color: #90a4ae;
  }
}
